import React from 'react';
import PropTypes from 'prop-types';
import AppBar from '@material-ui/core/AppBar';
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardHeader from '@material-ui/core/CardHeader';
import CssBaseline from '@material-ui/core/CssBaseline';
import Grid from '@material-ui/core/Grid';
// import StarIcon from '@material-ui/icons/StarBorder';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import { withStyles } from '@material-ui/core/styles';
import Avatar from '@material-ui/core/Avatar';
import iconeSJ from './assets/salvejorge.png';
import logoTV from './assets/televisa.png';
import logoTC from './assets/tvchile.png';
import iconeEV from './assets/espelhodavida.png';

const styles = theme => ({
  '@global': {
    body: {
      backgroundColor: theme.palette.common.white,
    },
  },
  appBar: {
    position: 'relative',
  },
  toolbarTitle: {
    flex: 1,
  },
  layout: {
    width: 'auto',
    marginLeft: theme.spacing.unit * 3,
    marginRight: theme.spacing.unit * 3,
    [theme.breakpoints.up(900 + theme.spacing.unit * 3 * 2)]: {
      width: 900,
      marginLeft: 'auto',
      marginRight: 'auto',
    },
  },
  heroContent: {
    maxWidth: 600,
    margin: '0',
    padding: `${theme.spacing.unit * 8}px 0 ${theme.spacing.unit * 6}px`,
  },
  cardPricing: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'baseline',
    marginBottom: theme.spacing.unit * 2,
  },
  cardActions: {
    backgroundColor: '#84ddc6',
    fontSize: 24,
  },
  footer: {
    marginTop: theme.spacing.unit * 8,
    borderTop: `1px solid ${theme.palette.divider}`,
    padding: `${theme.spacing.unit * 6}px 0`,
  },
});

const tiers = [
  {
    title: 'Salve Jorge',
    subheader: '(2012 - 2013)',
    icone: iconeSJ,
    logo: logoTV,
    description: ['10 users included', '2 GB of storage', 'Help center access', 'Email support'],
    grau: '90%',
    buttonVariant: 'outlined',
  },
  {
    title: 'Espelho da Vida',
    subheader: '(2002 - 2003)',
    icone: iconeEV,
    logo: '',
    description: ['10 users included', '2 GB of storage', 'Help center access', 'Email support'],
    grau: '80%',
    buttonVariant: 'outlined',
  },
  {
    title: 'Avenida Brasil',
    subheader: '(2012 - 2013)',
    icone: '',
    logo: logoTC,
    description: ['10 users included', '2 GB of storage', 'Help center access', 'Email support'],
    grau: '85%',
    buttonVariant: 'outlined',
  },
];
const footers = [
  {
    title: 'Company',
    description: ['Team', 'History', 'Contact us', 'Locations'],
  },
  {
    title: 'Features',
    description: ['Cool stuff', 'Random feature', 'Team feature', 'Developer stuff', 'Another one'],
  },
  {
    title: 'Resources',
    description: ['Resource', 'Resource name', 'Another resource', 'Final resource'],
  },
  {
    title: 'Legal',
    description: ['Privacy policy', 'Terms of use'],
  },
];

function Pricing(props) {
  const { classes } = props;

  return (
    <React.Fragment>
      <CssBaseline />
      <AppBar position="static" color="default" className={classes.appBar}>
        <Toolbar>
          <Typography variant="h6" color="inherit" noWrap className={classes.toolbarTitle}>
            GloboIndex
          </Typography>
          <Button color="primary" variant="outlined">
            Login
          </Button>
        </Toolbar>
      </AppBar>
      <main className={classes.layout}>
        {/* Hero unit */}
        <div className={classes.heroContent}>
          {/* <MediaCard /> */}
          <Typography component="h1" variant="h4" align="left" color="textPrimary" gutterBottom>
            Novelas
          </Typography>
        </div>
        {/* End hero unit */}
        <Grid container spacing={40} alignItems="flex-end">
          {tiers.map(tier => (
            // Enterprise card is full width at sm breakpoint
            <Grid item key={tier.title} xs={12} sm={6} md={4}>
              <Card>
                <CardHeader
                  title={tier.title}
                  subheader={tier.subheader}
                  titleTypographyProps={{ align: 'center', variant:'h6' }}
                  subheaderTypographyProps={{ align: 'center' }}
                  className={classes.cardHeader}
                  avatar={
                    <Avatar style={{marginLeft: '15px',  width: 60, height: 60}}>
                      {tier.icone && 
                        <img src={tier.icone} style={{ width: '75px' }} />
                      }
                      {!tier.icone && 
                        tier.title.charAt(0)
                      }
                    </Avatar>
                  }
                />
                <hr style={{width: '75%', backgroundColor: '#c4c4c4', height: '4px'}} />
                <CardContent>
                  <Typography variant="subtitle1" align="center" style={{ fontSize: '12px' }}>
                      parceiro sugerido<br/>
                      {tier.logo &&
                        <img src={tier.logo} alt="Logo" align='center' style={{ width: '120px' }} />
                      }
                      {!tier.logo &&
                        <Avatar style={{marginLeft: '15px', width: 60, height: 60}} align="center">
                        R
                        </Avatar>
                      }
                  </Typography>
                  <Typography variant="h6" align="center" style={{color:'#c4c4c4', fontWeight:'550'}} fontSize={24}>
                  {tier.grau} match
                  </Typography>
                </CardContent>
                <CardActions className={classes.cardActions}>
                  <Button fullWidth style={{color: 'white', border: 'none', textTransform: 'none', fontSize: '20px', fontWeight: 'bold'}} buttonStyle={{width:"100%"}}>
                    veja mais
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          ))}
        </Grid>
      </main>
    </React.Fragment>
  );
}

Pricing.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Pricing);
