import React from 'react';
import PropTypes from 'prop-types';
import AppBar from '@material-ui/core/AppBar';
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardHeader from '@material-ui/core/CardHeader';
import CssBaseline from '@material-ui/core/CssBaseline';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import { withStyles } from '@material-ui/core/styles';
import Avatar from '@material-ui/core/Avatar';
import iconeSJ from './assets/salvejorge.png';
import logoTV from './assets/televisa.png';
import { Link } from 'react-router-dom'

function Detail(props) {

  const { classes } = props;

  return (
    <React.Fragment>
      <CssBaseline />
      <AppBar position="static" color="default" className={classes.appBar}>
        <Toolbar>
          <Typography variant="h6" color="inherit" noWrap className={classes.toolbarTitle}>
            GloboIndex
          </Typography>
          <Button color="primary" variant="outlined">
            Login
          </Button>
        </Toolbar>
      </AppBar>
        <main className={classes.layout}>
          <div>
            <div className={classes.heroContent}>
                <Typography component="h1" variant="h4" align="left" color="textPrimary" gutterBottom>
                {tier.cliente}
                </Typography>
            </div>
              <Card>
                <Typography variant="subtitle1" align="center" style={{ fontSize: '12px' }}>
                {tier.title}
                </Typography>
                <Avatar style={{marginLeft: '15px',  width: 65, height: 50, borderRadius: 0}}>
                {tier.icone && 
                    <img src={tier.icone} style={{ width: '75px' }} />
                }
                {!tier.icone && 
                    <Typography>
                        {tier.icone}
                    </Typography>
                }
                </Avatar>
                <Typography variant="grau" align="right" style={{ fontSize: '12px' }}>
                {tier.grau} match
                </Typography>

                <Line color="#c4c4c4" opacity="1.0"/>

                <CardContent>
                  <Typography>
                    Público-alvo
                  </Typography>                   
                  <Typography>
                    mulheres, 18-25 anos
                  </Typography>
                  <Typography>
                    casse B2
                  </Typography>  
                </CardContent>

                <Line color="#c4c4c4" opacity="0.6"/>

                <CardContent>
                  <Typography>
                    Últimos negócios
                  </Typography>     
                  <Typography>
                    Caminho das Índias,
                  </Typography>
                  <Typography>
                    Da Cor do Pecado
                  </Typography>                
                </CardContent>

                <Line color="#c4c4c4" opacity="0.6"/>

                <CardContent>
                  <Typography>
                    Novelas no ar
                  </Typography> 
                  <Typography>
                    El Mundo de Ramón, 60 dias para acabar
                  </Typography>
                  <Typography>
                    Caminho das Índias, 45 dias para acabar
                  </Typography>                    
                </CardContent>

                <CardActions className={classes.cardActions}>
                  <Button fullWidth style={{color: 'white', border: 'none', textTransform: 'none', fontSize: '20px', fontWeight: 'bold'}} buttonStyle={{width:"100%"}}>
                    <Link style={{color: 'white', border: 'none', textTransform: 'none', fontSize: '20px', fontWeight: 'bold', textDecoration: 'none'}} to="/Detail">cadastrar feedback</Link>
                  </Button>
                </CardActions>
              </Card>
            </div>
      </main>
    </React.Fragment>
  );
}

Detail.propTypes = {
  classes: PropTypes.object.isRequired,
};

const Line = ({ color, opacity }) => (
  <hr
      style={{
          width: '75%',
          color: color,
          backgroundColor: color,
          height: '3px',
          border: 'none', 
          opacity: opacity,
      }}
  />
);

const styles = theme => ({
  '@global': {
    body: {
      backgroundColor: theme.palette.common.white,
    },
  },
  appBar: {
    position: 'relative',
  },
  toolbarTitle: {
    flex: 1,
  },
  layout: {
    width: 'auto',
    marginLeft: theme.spacing.unit * 3,
    marginRight: theme.spacing.unit * 3,
    [theme.breakpoints.up(900 + theme.spacing.unit * 3 * 2)]: {
      width: 900,
      marginLeft: 'auto',
      marginRight: 'auto',
    },
  },
  heroContent: {
    maxWidth: 600,
    margin: '0',
    padding: `${theme.spacing.unit * 8}px 0 ${theme.spacing.unit * 6}px`,
  },
  cardPricing: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'baseline',
    marginBottom: theme.spacing.unit * 2,
  },
  cardActions: {
    backgroundColor: '#84ddc6',
    fontSize: 24,
  },
});

const tier = {
    title: 'Salve Jorge',
    subheader: 'México',
    cliente: 'TeleVisa Inc.',
    grau: '90%',
    icone: iconeSJ,
    logo: logoTV,
    description: ['10 users included', '2 GB of storage', 'Help center access', 'Email support'],
    buttonVariant: 'outlined',
};

export default withStyles(styles)(Detail);
