import React, { Component } from 'react';
import './App.css';
import Pricing from './Pricing.js';

const API = 'https://globoindex.tk/api/salles';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      data: null
    }
  }

  componentWillMount() {
    this.getData();
  }

  async getData(){
    const res = await fetch(API);
    const data = await res.json();
    return this.setState({data});
 }

  componentDidMount() {
    this.getData();
  }

  render(){
    return (
      this.state && <ul>{this.state.data.map(item => <li>{item.title}</li>)} </ul>
    )
  }

  // render() {
  //   return (
  //    <Pricing></Pricing>
  //   );
  // }
}

export default App;
